$(document).ready(function(){$("div.cr.button.pulse, button.cr.button.pulse, a.menu-item.pulse").click(function(t){t.preventDefault(),$(".ripple").remove();var e=$(this).offset().left,p=$(this).offset().top,i=$(this).width(),s=$(this).height();$(this).prepend("<span class='ripple'></span>"),s<=i?s=i:i=s;var a=t.pageX-e-i/2,l=t.pageY-p-s/2;$(".ripple").css({width:i,height:s,top:l+"px",left:a+"px"}).addClass("ripple-active")})});